INSERT INTO tb_expedition (title,photo,link_h,intro_text,country_id,title_extra,show_country,publish,link_on_off,im_w,im_h,hide_date_operator,hide_date,duration,date,creation_date,conditions,hotel_publish,hotel_day1,hotel_date_hide,hotel_cat_hide,hotel_address_hide,hotel_tel_hide,hotel_email_hide,hotel_web_hide,hotel_photo_hide,hotel_col_hide_details) VALUES ('Nuno Alves prog 1','Botswana-sum-site.jpg', 0,'<br><b>BOTSWANA</b><br><br>Quando presa e predador bebem água lado a lado, há algo de diferente no ar. Talvez seja porque, num capricho da natureza, todo o delta de um rio desaparece em meio ao nada. Talvez seja apenas sede.',30,1,1,0,0,250,243,0,0,18,'2025-12-01','2025-11-10',0,1,'2025-12-01',0,0,0,0,0,0,0,0);
