insert into tb_country_attributes (country_id,attribute_id,description) VALUES (30,'WHEN_GO','<br>Botswana. Não há meses ruins para visitar o Botswana. As chuvas, quando acontecem nunca atrapalham, e o calor nunca é um exagero. ');
insert into tb_country_attributes (country_id,attribute_id,description) VALUES (31,'WHEN_GO','<br>Zimbabwe. Não há meses ruins para visitar o Zimbabwe. As chuvas, quando acontecem nunca atrapalham, e o calor nunca é um exagero. ');
insert into tb_country_attributes (country_id,attribute_id,description) VALUES (84,'WHEN_GO','<br>Moçambique. Não há meses ruins para visitar o Moçambique. As chuvas, quando acontecem nunca atrapalham, e o calor nunca é um exagero. ');
insert into tb_country_attributes (country_id,attribute_id,description) values (30,'MONEY','<br>Botswana. Não é preciso muito. Os cafés da manhã (todos inclusos) são bons e fartos. Almoços custam pouco: não mais que US$10 cada, para uma refeição completa. Os jantares estão inclusos.' );
