insert into tb_country_attributes (country_id,attribute_id,description) values (30,'MORE_TIME','<br>Botswana. Sim. A Highland tem diversas opções de extensões África adentro, tais como Botswana, África do Sul, Namíbia, Seychelles, Kenya, e Tanzânia com ou sem o Kilimanjaro.  ' );
