insert into tb_country_attributes (country_id,attribute_id,description) values (30,'BOOKS','<br>Botswana. A "Apa Publications" produz uma série excelente de guias de viagem chamada "Insight Guides", a venda em várias livrarias no Brasil. Além disso, qualquer literatura sobre o comportamento de animais em seu habitat natural é de extrema utilidade para formar uma plataforma de conhecimentos básicos sobre a África que lhe espera.  ' );
insert into tb_country_attributes (country_id,attribute_id,description) values (31,'BOOKS','<br>Zimbabwe. A "Apa Publications" produz uma série excelente de guias de viagem chamada "Insight Guides", a venda em várias livrarias no Brasil. Além disso, qualquer literatura sobre o comportamento de animais em seu habitat natural é de extrema utilidade para formar uma plataforma de conhecimentos básicos sobre a África que lhe espera.  ' );
