insert into tb_country_attributes (country_id,attribute_id,description) values (30,'PLANES','Botswana. Os v&ocirc;os intercontinentais s&atilde;o, em geral, com aparelhos Boing 747-400. Os v&ocirc;os envolvendo Paro s&atilde;o com aparelhos&nbsp;novoa Air Bus. No coments.&nbsp;A Druk Air, que voa estes setores, &eacute; radicalmente categ&oacute;rica quanto ao limite de 20 kg de bagagem.  ' );
