insert into tb_country_attributes (country_id,attribute_id,description) values (30,'HOTELS','<br>Botswana. Os hotéis nas cidades são todos excelentes. Para sua surpresa, o acampamento bérbere é confortabilíssimo, aquecido com braseiros no inverno, e tem banho quente (não privativo à sua tenda). ' );
